import { cn } from '@/lib/utils';
import { Marquee } from '@/components/ui/marquee';

const logos = [
  {
    name: 'Microsoft',
    img: 'https://cdn.simpleicons.org/microsoft/000/fff',
  },
  {
    name: 'Apple',
    img: 'https://cdn.simpleicons.org/apple/000/fff',
  },
  {
    name: 'Google',
    img: 'https://cdn.simpleicons.org/google/000/fff',
  },
  {
    name: 'Facebook',
    img: 'https://cdn.simpleicons.org/facebook/000/fff',
  },
  {
    name: 'LinkedIn',
    img: 'https://cdn.simpleicons.org/linkedin/000/fff',
  },
  {
    name: 'Twitter',
    img: 'https://cdn.simpleicons.org/twitter/000/fff',
  },
];

const Logo = ({ name, img }: { name: string; img: string }) => {
  return (
    <div className={cn('h-12 w-12 cursor-pointer')}>
      <img src={img} alt={name} />
    </div>
  );
};

const MarqueeLogos = () => {
  return (
    <div className='relative flex h-full w-full flex-col items-center justify-center gap-4 overflow-hidden rounded-lg border dark:bg-neutral-800 bg-neutral-50 py-20 md:shadow-xl'>
      <Marquee className='[--gap:3rem]'>
        {logos.map((logo, idx) => (
          <Logo key={idx} {...logo} />
        ))}
      </Marquee>
      <div className='pointer-events-none absolute inset-y-0 left-0 w-1/3 bg-linear-to-r from-white dark:from-background'></div>
      <div className='pointer-events-none absolute inset-y-0 right-0 w-1/3 bg-linear-to-l from-white dark:from-background'></div>
    </div>
  );
};

export default MarqueeLogos;

import { cn } from '@/lib/utils'

interface MarqueeProps {
  className?: string
  reverse?: boolean
  pauseOnHover?: boolean
  children?: React.ReactNode
  vertical?: boolean
  repeat?: number
  [key: string]: any
}

export function Marquee({
  className,
  reverse,
  pauseOnHover = false,
  children,
  vertical = false,
  repeat = 4,
  ...props
}: MarqueeProps) {
  return (
    <div
      {...props}
      className={cn(
        'group flex overflow-hidden p-2 [--duration:40s] [--gap:1rem] gap-(--gap)',
        {
          'flex-row': !vertical,
          'flex-col': vertical,
        },
        className
      )}
    >
      {Array(repeat)
        .fill(0)
        .map((_, i) => (
          <div
            key={i}
            className={cn('flex shrink-0 justify-around gap-(--gap)', {
              'animate-marquee flex-row': !vertical && !reverse,
              'animate-marquee-vertical flex-col': vertical && !reverse,
              'animate-marquee-reverse flex-row': !vertical && reverse,
              'animate-marquee-vertical-reverse flex-col': vertical && reverse,
              'group-hover:[animation-play-state:paused]': pauseOnHover,
            })}
          >
            {children}
          </div>
        ))}
    </div>
  )
}
